    public void renew() {
        double[] a = new double[3];
        a[0] = ((LPBar)barRed).getValue() / 100.0;
        a[1] = ((LPBar)barGreen).getValue() / 100.0;
        a[2] = ((LPBar)barBlue).getValue() / 100.0;
        ((RecommendPane) rmPane).reRecommend(a);
    }

