package db;import java.io.File;import java.util.ArrayList;import java.util.Iterator;import java.util.List;import jena.OwlFactory;import org.w3c.dom.NodeList;import util.Constant;import com.hp.hpl.jena.ontology.Individual;import com.hp.hpl.jena.ontology.OntClass;import com.hp.hpl.jena.ontology.OntModel;import com.hp.hpl.jena.rdf.model.Bag;import com.hp.hpl.jena.rdf.model.Model;import com.hp.hpl.jena.rdf.model.RDFList;import com.hp.hpl.jena.rdf.model.RDFNode;import com.hp.hpl.jena.rdf.model.ResIterator;import com.hp.hpl.jena.rdf.model.Resource;import com.hp.hpl.jena.rdf.model.Statement;import com.hp.hpl.jena.rdf.model.StmtIterator;import com.hp.hpl.jena.util.iterator.ExtendedIterator;import com.hp.hpl.jena.vocabulary.VCARD;public class ConceptReader {	public static ArrayList<Individual> getAllConcepts(){		OntModel model = OwlFactory.getOntOWLModel();		ArrayList <Individual> concepts = new ArrayList<Individual>();		OntClass concept = model.getOntClass(Constant.NS+"E_Concept");		Iterator <Individual>iter2 = model.listIndividuals();		while(iter2.hasNext()){			Individual indi= (Individual) iter2.next();			if(concept.equals(indi.getOntClass())){				concepts.add(indi);				System.out.println(indi.getLocalName());			}		}		return concepts;	}	public static ArrayList<Individual> getAllResources(){		OntModel model = OwlFactory.getOntOWLModel();		ArrayList <Individual> resources = new ArrayList<Individual>();		OntClass resource = model.getOntClass(Constant.NS+"E_Resource");		Iterator <Individual>iter2 = model.listIndividuals();		while(iter2.hasNext()){			Individual indi= (Individual) iter2.next();			if(indi==null)break;			if(resource.equals(indi.getOntClass())){				resources.add(indi);				System.out.println(indi.getLocalName());			}		}		return resources;	}	/**	 * @param args	 */	public static void main(String[] args) throws Exception {		ArrayList<Individual> i = getAllResources();		System.out.println(i.size());	}}